package hexlet.code.games;import hexlet.code.Engine;import static hexlet.code.Utils.generateNumber;public final class Even {    static final int MIN = 1;    static final int MAX = 100;    static final int ROUNDS = 3;    static final String DESC = "Answer 'yes' if the number is even, otherwise answer 'no'.";    public static boolean isEven(int i) {        return (i % 2) == 0;    }    public static void parityCheck() {        int j = 0;        String[][] answ = new String[ROUNDS][2];        for (int counter = 0; counter < ROUNDS; counter++) {            int randomNumber = generateNumber(MIN, MAX);            String rightAnswer = isEven(randomNumber) ? "yes" : "no";            String question = String.valueOf(randomNumber);            answ[counter][j] = rightAnswer;            j++;            answ[counter][j] = question;            j = 0;        }        Engine.run(DESC, answ);    }}